import React, { useState } from 'react';
import './ViewTreatmentProfile.css';

function ViewTreatmentProfile() {
  const [selectedSymptom, setSelectedSymptom] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 2; // Số mục hiển thị trên mỗi trang

  const handleSymptomClick = (symptom) => {
    setSelectedSymptom(symptom);
  };

  const handleClosePopUp = () => {
    setSelectedSymptom(null);
  };

  const profile = {
    name: 'John Doe',
    id: '123456',
    gender: 'Male',
    doctorName: 'Dr. Jane Smith',
    treatmentDate: 'June 28, 2023',
    symptoms: [
      { name: 'Fever', description: 'A high body temperature' },
      { name: 'Headache', description: 'Pain in the head' },
      { name: 'Cough', description: 'Expelling air from the lungs' }
      // Thêm các symptoms khác vào đây
    ],
    treatmentProgress: [
      { time: '10:00 AM', date: 'June 28, 2023' },
      { time: '11:30 AM', date: 'June 29, 2023' },
      { time: '09:45 AM', date: 'June 30, 2023' }
      // Thêm các treatmentProgress khác vào đây
    ]
  };

  const handleConfirm = () => {
    // Xử lý logic xác nhận ở đây
    alert('Profile confirmed');
  };

  // Tính toán chỉ mục bắt đầu và chỉ mục kết thúc của mục trên trang hiện tại
  const indexOfLastSymptom = currentPage * itemsPerPage;
  const indexOfFirstSymptom = indexOfLastSymptom - itemsPerPage;
  const currentSymptoms = profile.symptoms.slice(indexOfFirstSymptom, indexOfLastSymptom);

  // Tính toán số trang dựa trên số lượng symptoms
  const totalPages = Math.ceil(profile.symptoms.length / itemsPerPage);

  // Chuyển đến trang tiếp theo
  const nextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  // Chuyển đến trang trước đó
  const prevPage = () => {
    setCurrentPage(currentPage - 1);
  };

  // Chuyển đến trang đầu tiên
  const firstPage = () => {
    setCurrentPage(1);
  };

  // Chuyển đến trang cuối cùng
  const lastPage = () => {
    setCurrentPage(totalPages);
  };

  return (
    <div className="profile-container">
      <h2>View Profile</h2>
      <div className="profile-details">
        <p><strong>Name:</strong> {profile.name}</p>
        <p><strong>ID:</strong> {profile.id}</p>
        <p><strong>Gender:</strong> {profile.gender}</p>
        <p><strong>Doctor Name:</strong> {profile.doctorName}</p>
        <p><strong>Treatment Date:</strong> {profile.treatmentDate}</p>
        <div className="symptom-list">
          <h3>Symptoms:</h3>
          {currentSymptoms.map((symptom, index) => (
            <div
              key={index}
              className="symptom"
              onClick={() => handleSymptomClick(symptom)}
            >
              <span>{symptom.name}</span>
            </div>
          ))}
        </div>
        {/* Phân trang cho phần Symptoms */}
        <div className="pagination">
          <button onClick={firstPage} disabled={currentPage === 1}>
            First
          </button>
          <button onClick={prevPage} disabled={currentPage === 1}>
            Prev
          </button>
          <span>
            Page {currentPage} of {totalPages}
          </span>
          <button onClick={nextPage} disabled={currentPage === totalPages}>
            Next
          </button>
          <button onClick={lastPage} disabled={currentPage === totalPages}>
            Last
          </button>
        </div>
        <div className="treatment-progress">
          <h3>Treatment Progress</h3>
          {profile.treatmentProgress.map((progress, index) => (
            <div key={index} className="treatment-box">
              <p>{progress.time}</p>
              <p>{progress.date}</p>
            </div>
          ))}
        </div>
      </div>
      <button className="confirm-button" onClick={handleConfirm}>Confirm</button>

      {selectedSymptom && (
        <div>
          <div className="overlay"></div>
          <div className="pop-up">
            {/* Nội dung pop-up */}
            <h3>{selectedSymptom.name}</h3>
            <p>{selectedSymptom.description}</p>
            <button className="close-button" onClick={handleClosePopUp}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewTreatmentProfile;
