import React, { useState } from 'react';

const ViewBooking = () => {
  const [selectedBooking, setSelectedBooking] = useState(null);
  const currentDate = new Date().toLocaleDateString('vi-VN', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
  });

  const bookingList = [
    {
      id: 1,
      patientName: 'Nguyễn Văn A',
      reason: 'Đau bụng',
      date: currentDate,
      time: '08:00',
      status: 'Đang chờ',
    },
    {
      id: 2,
      patientName: 'Trần Thị B',
      reason: 'Sốt cao',
      date: currentDate,
      time: '09:30',
      status: 'Đang chờ',
    },
    {
      id: 3,
      patientName: 'Lê Văn C',
      reason: 'Đau họng',
      date: currentDate,
      time: '11:15',
      status: 'Đã hủy',
    },
    {
      id: 4,
      patientName: 'Phạm Thị D',
      reason: 'Đau mắt',
      date: '04/07/2023',
      time: '14:30',
      status: 'Đang chờ',
    },
    {
      id: 5,
      patientName: 'Nguyễn Văn E',
      reason: 'Sưng mũi',
      date: '04/07/2023',
      time: '15:45',
      status: 'Đang chờ',
    },
    {
      id: 6,
      patientName: 'Trần Thanh F',
      reason: 'Đau tai',
      date: '04/07/2023',
      time: '17:00',
      status: 'Đang chờ',
    },
    {
      id: 7,
      patientName: 'Lê Văn G',
      reason: 'Sổ mũi',
      date: '04/07/2023',
      time: '18:15',
      status: 'Đang chờ',
    },
    {
      id: 8,
      patientName: 'Nguyễn Thị H',
      reason: 'Đau răng',
      date: '04/07/2023',
      time: '19:30',
      status: 'Đang chờ',
    },
    {
      id: 9,
      patientName: 'Phạm Văn I',
      reason: 'Đau mắt',
      date: '04/07/2023',
      time: '20:45',
      status: 'Đang chờ',
    },
    {
      id: 10,
      patientName: 'Trần Thị K',
      reason: 'Sưng mũi',
      date: '04/07/2023',
      time: '22:00',
      status: 'Đang chờ',
    },
    {
      id: 11,
      patientName: 'Lê Văn L',
      reason: 'Đau cổ',
      date: '05/07/2023',
      time: '08:00',
      status: 'Đang chờ',
    },
    {
      id: 12,
      patientName: 'Nguyễn Thị M',
      reason: 'Đau bụng',
      date: '05/07/2023',
      time: '09:30',
      status: 'Đang chờ',
    },
    {
      id: 13,
      patientName: 'Trần Văn N',
      reason: 'Sốt cao',
      date: '05/07/2023',
      time: '11:15',
      status: 'Đang chờ',
    },
    {
      id: 14,
      patientName: 'Lê Thị P',
      reason: 'Đau họng',
      date: '05/07/2023',
      time: '14:30',
      status: 'Đang chờ',
    },
    {
      id: 15,
      patientName: 'Nguyễn Văn Q',
      reason: 'Sưng mũi',
      date: '05/07/2023',
      time: '15:45',
      status: 'Đang chờ',
    },
    {
      id: 16,
      patientName: 'Phạm Thị R',
      reason: 'Đau mắt',
      date: '05/07/2023',
      time: '17:00',
      status: 'Đang chờ',
    },
    {
      id: 17,
      patientName: 'Trần Văn S',
      reason: 'Sổ mũi',
      date: '05/07/2023',
      time: '18:15',
      status: 'Đang chờ',
    },
    {
      id: 18,
      patientName: 'Lê Thị T',
      reason: 'Đau răng',
      date: '05/07/2023',
      time: '19:30',
      status: 'Đang chờ',
    },
    {
      id: 19,
      patientName: 'Phạm Văn U',
      reason: 'Đau mắt',
      date: '05/07/2023',
      time: '20:45',
      status: 'Đang chờ',
    },
    {
      id: 20,
      patientName: 'Trần Thị V',
      reason: 'Sưng mũi',
      date: '05/07/2023',
      time: '22:00',
      status: 'Đang chờ',
    },
  ];

  const handleViewBooking = (booking) => {
    setSelectedBooking(booking);
  };

  const handleCancelBooking = (booking) => {
    const updatedBookingList = bookingList.map((item) =>
      item.id === booking.id ? { ...item, status: 'Đang chờ hủy' } : item
    );
    setSelectedBooking({ ...booking, status: 'Đang chờ hủy' });
    setBookingList(updatedBookingList);
  };

  const handleMarkCancelled = (booking) => {
    const updatedBookingList = bookingList.map((item) =>
      item.id === booking.id ? { ...item, status: 'Đã hủy' } : item
    );
    setSelectedBooking({ ...booking, status: 'Đã hủy' });
    setBookingList(updatedBookingList);
  };

  return (
    <div className="view-booking-container">
      <h2 className="booking-heading">Lịch khám hôm nay ({currentDate})</h2>
      <ul className="booking-list">
        {bookingList.map((booking) => (
          <li
            key={booking.id}
            className={`booking-item ${booking.status === 'Đã hủy' ? 'cancelled' : ''}`}
          >
            <span className="patient-name">{booking.patientName}</span>
            <span className="booking-info">
              Ngày: {booking.date} - Giờ: {booking.time}
            </span>
            <span className="booking-status">{booking.status}</span>
            {booking.status === 'Đang chờ' && (
              <button className="cancel-button" onClick={() => handleCancelBooking(booking)}>
                Hủy
              </button>
            )}
            {booking.status === 'Đang chờ hủy' && (
              <button className="mark-cancelled-button" onClick={() => handleMarkCancelled(booking)}>
                Đã hủy
              </button>
            )}
          </li>
        ))}
      </ul>
      {selectedBooking && (
        <div className="selected-booking">
          <h3>Thông tin phiếu khám đã chọn:</h3>
          <p>Patient Name: {selectedBooking.patientName}</p>
          <p>Reason: {selectedBooking.reason}</p>
          <p>Date: {selectedBooking.date}</p>
          <p>Time: {selectedBooking.time}</p>
          <p>Status: {selectedBooking.status}</p>
        </div>
      )}
    </div>
  );
};

export default ViewBooking;
