import React, { useState } from 'react';
import './SymptomList.css';

function SymptomList({ symptoms }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [newSymptom, setNewSymptom] = useState('');
  const [showAddSymptomModal, setShowAddSymptomModal] = useState(false);
  const [createdSymptoms, setCreatedSymptoms] = useState([]);

  const handleAddSymptom = (symptom) => {
    // Handle adding a symptom to the profile
    console.log('Added Symptom:', symptom);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleNewSymptomInputChange = (event) => {
    setNewSymptom(event.target.value);
  };

  const handleCreateNewSymptom = () => {
    if (newSymptom) {
      setCreatedSymptoms([...createdSymptoms, newSymptom]);
      setNewSymptom('');
      setShowAddSymptomModal(false);
      handleAddSymptom(newSymptom);
    }
  };

  const openAddSymptomModal = () => {
    setShowAddSymptomModal(true);
  };

  const closeAddSymptomModal = () => {
    setShowAddSymptomModal(false);
  };

  const filteredSymptoms = symptoms.filter((symptom) =>
    symptom.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <ul className="symptom-list">
        {symptoms.map((symptom, index) => (
          <li key={index}>{symptom}</li>
        ))}
      </ul>
      <div className="add-symptom">
        <input
          type="text"
          placeholder="Search or create new symptom"
          value={searchTerm}
          onChange={handleSearch}
        />
        <button onClick={openAddSymptomModal}>Add</button>
      </div>
      {showAddSymptomModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeAddSymptomModal}>&times;</span>
            <h3>Create New Symptom</h3>
            <div className="new-symptom-form">
              <label>Symptom Name:</label>
              <input
                type="text"
                value={newSymptom}
                onChange={handleNewSymptomInputChange}
              />
              <button onClick={handleCreateNewSymptom}>Create</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SymptomList;
