import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
    const [currentBookingsPage, setCurrentBookingsPage] = useState(1);
    const [futureBookingsPage, setFutureBookingsPage] = useState(1);
    const bookingsPerPage = 5;

    const initialBookingList = [
        {
            id: 1,
            avatar: 'avatar_url_1',
            service: 'Khám tổng quát',
            status: 'Đã khám',
            date: '2023-07-01',
        },
        {
            id: 2,
            avatar: 'avatar_url_2',
            service: 'Lấy men răng',
            status: 'Đang chờ khám',
            date: '2023-07-01',
        },
        {
            id: 3,
            avatar: 'avatar_url_3',
            service: 'Nhổ răng khôn',
            status: 'Đang chờ khám',
            date: '2023-07-01',
        },
        {
            id: 4,
            avatar: 'avatar_url_4',
            service: 'Lấy tủy',
            status: 'Đang chờ khám',
            date: '2023-07-02',
        },
        {
            id: 5,
            avatar: 'avatar_url_5',
            service: 'Cạo vôi răng',
            status: 'Đang chờ khám',
            date: '2023-07-03',
        },
        {
            id: 6,
            avatar: 'avatar_url_6',
            service: 'Trám răng',
            status: 'Đã khám',
            date: '2023-07-05',
        },
        {
            id: 7,
            avatar: 'avatar_url_7',
            service: 'Tẩy trắng răng',
            status: 'Đã khám',
            date: '2023-07-05',
        },
        {
            id: 8,
            avatar: 'avatar_url_8',
            service: 'Chỉnh nha',
            status: 'Đang chờ khám',
            date: '2023-07-06',
        },
        {
            id: 9,
            avatar: 'avatar_url_9',
            service: 'Niềng răng',
            status: 'Đang chờ khám',
            date: '2023-07-06',
        },
        {
            id: 10,
            avatar: 'avatar_url_10',
            service: 'Trồng răng',
            status: 'Đã khám',
            date: '2023-07-07',
        },
        {
            id: 11,
            avatar: 'avatar_url_11',
            service: 'Tạo hình răng',
            status: 'Đã khám',
            date: '2023-07-07',
        },
        {
            id: 12,
            avatar: 'avatar_url_12',
            service: 'Chỉnh hình răng',
            status: 'Đang chờ khám',
            date: '2023-07-08',
        },
        {
            id: 13,
            avatar: 'avatar_url_13',
            service: 'Trám hình răng',
            status: 'Đang chờ khám',
            date: '2023-07-08',
        },
        {
            id: 14,
            avatar: 'avatar_url_14',
            service: 'Lấy cao răng',
            status: 'Đã khám',
            date: '2023-07-09',
        },
        {
            id: 15,
            avatar: 'avatar_url_15',
            service: 'Phục hình răng',
            status: 'Đã khám',
            date: '2023-07-09',
        },
    ];

    const futureBookings = [
        {
            id: 4,
            avatar: 'avatar_url_4',
            service: 'Lấy tủy',
            status: 'Đang chờ khám',
            date: '2023-07-02',
        },
        {
            id: 5,
            avatar: 'avatar_url_5',
            service: 'Cạo vôi răng',
            status: 'Đang chờ khám',
            date: '2023-07-03',
        },
        {
            id: 6,
            avatar: 'avatar_url_6',
            service: 'Trám răng',
            status: 'Đã khám',
            date: '2023-07-05',
        },
        // ...Thêm dữ liệu phiếu đặt lịch khác tương tự cho các ngày tương lai
    ];

    const [bookingList, setBookingList] = useState(initialBookingList);
    const totalBookingsPages = Math.ceil(bookingList.length / bookingsPerPage);
    const totalFuturePages = Math.ceil(futureBookings.length / bookingsPerPage);

    const cancelBooking = (bookingId) => {
        const updatedBookingList = bookingList.map((booking) =>
            booking.id === bookingId && booking.status !== 'Đã hủy'
                ? { ...booking, status: 'Đã hủy' }
                : booking
        );
        setBookingList(updatedBookingList);
    };

    const requestCancellation = (bookingId) => {
        const updatedBookingList = bookingList.map((booking) =>
            booking.id === bookingId
                ? { ...booking, status: 'Đang chờ hủy đơn' }
                : booking
        );
        setBookingList(updatedBookingList);
    };

    const renderBookings = () => {
        const startIndex = (currentBookingsPage - 1) * bookingsPerPage;
        const endIndex = startIndex + bookingsPerPage;
        const currentBookings = bookingList.slice(startIndex, endIndex);

        return currentBookings.map((booking) => (
            <div className="booking-item" key={booking.id}>
                {/* Hiển thị thông tin phiếu đặt lịch khám */}
            </div>
        ));
    };

    const renderSchedule = () => {
        const startIndex = (futureBookingsPage - 1) * bookingsPerPage;
        const endIndex = startIndex + bookingsPerPage;
        const currentBookings = futureBookings.slice(startIndex, endIndex);

        return currentBookings.map((booking) => (
            <div className="booking-item" key={booking.id}>
                {/* Hiển thị thông tin phiếu đặt lịch khám trong tương lai */}
            </div>
        ));
    };

    const renderBookingsPagination = () => {
        const paginationItems = [];

        for (let i = 1; i <= totalBookingsPages; i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === currentBookingsPage ? 'active' : ''}`}
                    onClick={() => setCurrentBookingsPage(i)}
                >
                    {i}
                </button>
            );
        }

        return (
            <div className="pagination">
                {paginationItems}
            </div>
        );
    };

    const renderFutureBookingsPagination = () => {
        const paginationItems = [];

        for (let i = 1; i <= totalFuturePages; i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === futureBookingsPage ? 'active' : ''}`}
                    onClick={() => setFutureBookingsPage(i)}
                >
                    {i}
                </button>
            );
        }

        return (
            <div className="pagination">
                {paginationItems}
            </div>
        );
    };

    const getCurrentDate = () => {
        const currentDate = new Date();
        currentDate.setHours(0, 0, 0, 0);
        return currentDate.toISOString().split('T')[0];
    };

    return (
        <div className="view-booking">
            <h1>Danh sách phiếu đặt lịch khám</h1>
            <h2>Hôm nay, Ngày {getCurrentDate()}</h2>
            <div className="booking-list">{renderBookings()}</div>
            {renderBookingsPagination()}
            <div className="schedule">
                <h2>Lịch trình khám</h2>
                <div className="booking-list">{renderSchedule()}</div>
                {renderFutureBookingsPagination()}
            </div>
        </div>
    );
};

export default ViewBooking;
