import React, { useState } from 'react';
import './AdminCreateSlot.css';

function AdminCreateSlot() {
    const [selectedDoctor, setSelectedDoctor] = useState(null);
    const [startDate, setStartDate] = useState(null);
    const [endDate, setEndDate] = useState(null);
    const [selectedShifts, setSelectedShifts] = useState([]);
    const [isScheduleCreated, setIsScheduleCreated] = useState(false);

    const doctorList = [
        // Danh sách các bác sĩ trong hệ thống
    ];

    const handleSelectDoctor = (doctor) => {
        setSelectedDoctor(doctor);
    };

    const handleSelectStartDate = (date) => {
        setStartDate(date);
    };

    const handleSelectEndDate = (date) => {
        setEndDate(date);
    };

    const handleSelectShift = (shift) => {
        const updatedShifts = [...selectedShifts];
        if (updatedShifts.includes(shift)) {
            updatedShifts.splice(updatedShifts.indexOf(shift), 1);
        } else {
            updatedShifts.push(shift);
        }
        setSelectedShifts(updatedShifts);
    };

    const handleSaveSchedule = () => {
        if (selectedDoctor && startDate && endDate && selectedShifts.length > 0) {
            setIsScheduleCreated(true);
        } else {
            // Hiển thị thông báo lỗi hoặc yêu cầu người dùng nhập đầy đủ thông tin
        }
    };

    return (
        <div className="admin-create-slot">
            <h2 className="admin-create-slot__title">Tạo Lịch Làm Việc</h2>

            <div className="admin-create-slot__section">
                <h3 className="admin-create-slot__section-title">Chọn bác sĩ</h3>
                <ul className="admin-create-slot__doctor-list">
                    {doctorList.map((doctor) => (
                        <li
                            key={doctor.id}
                            className={`admin-create-slot__doctor-item ${selectedDoctor === doctor ? 'selected' : ''}`}
                            onClick={() => handleSelectDoctor(doctor)}
                        >
                            <img src={doctor.image} alt={doctor.name} />
                            <p>{doctor.name}</p>
                        </li>
                    ))}
                </ul>
            </div>

            <div className="admin-create-slot__section">
                <h3 className="admin-create-slot__section-title">Chọn khoảng thời gian</h3>
                <label htmlFor="startDate" className="admin-create-slot__label">
                    Ngày bắt đầu:
                    <input
                        type="date"
                        id="startDate"
                        value={startDate}
                        onChange={(e) => handleSelectStartDate(e.target.value)}
                        className="admin-create-slot__input"
                    />
                </label>

                <label htmlFor="endDate" className="admin-create-slot__label">
                    Ngày kết thúc:
                    <input
                        type="date"
                        id="endDate"
                        value={endDate}
                        onChange={(e) => handleSelectEndDate(e.target.value)}
                        className="admin-create-slot__input"
                    />
                </label>
            </div>

            <div className="admin-create-slot__section">
                <h3 className="admin-create-slot__section-title">Chọn ca làm việc</h3>
                <div className="admin-create-slot__shift-item">
                    <input
                        type="checkbox"
                        id="shift1"
                        checked={selectedShifts.includes('Ca sáng')}
                        onChange={() => handleSelectShift('Ca sáng')}
                        className="admin-create-slot__checkbox"
                    />
                    <label htmlFor="shift1" className="admin-create-slot__label">Ca sáng</label>
                </div>

                <div className="admin-create-slot__shift-item">
                    <input
                        type="checkbox"
                        id="shift2"
                        checked={selectedShifts.includes('Ca chiều')}
                        onChange={() => handleSelectShift('Ca chiều')}
                        className="admin-create-slot__checkbox"
                    />
                    <label htmlFor="shift2" className="admin-create-slot__label">Ca chiều</label>
                </div>
            </div>

            <div className="admin-create-slot__section">
                <button onClick={handleSaveSchedule} className="admin-create-slot__button">Lưu</button>
            </div>

            {isScheduleCreated && (
                <div className="admin-create-slot__section">
                    <h3 className="admin-create-slot__section-title">Lịch làm việc đã tạo</h3>
                    <p>Bác sĩ: {selectedDoctor.name}</p>
                    <p>Ngày bắt đầu: {startDate}</p>
                    <p>Ngày kết thúc: {endDate}</p>
                    <p>Ca làm việc: {selectedShifts.join(', ')}</p>
                </div>
            )}
        </div>
    );
}

export default AdminCreateSlot;
