import React, { useState } from 'react';
import './ViewTreatmentProfile.css';
import SymptomList from './SymptomList/SymptomList';

function ViewTreatmentProfile() {
  const [symptoms, setSymptoms] = useState(['Fever', 'Headache', 'Cough']);
  const [showSymptomList, setShowSymptomList] = useState(false);

  const handleAddSymptom = (symptom) => {
    setSymptoms([...symptoms, symptom]);
  };

  const handleDeleteSymptom = (index) => {
    const updatedSymptoms = [...symptoms];
    updatedSymptoms.splice(index, 1);
    setSymptoms(updatedSymptoms);
  };

  const handleConfirm = () => {
    alert('Profile confirmed');
    // Logic to confirm the profile
    // ...
  };

  return (
    <div className="profile-container">
      <h2>View Profile</h2>
      <div className="profile-details">
        <p><strong>Name:</strong> John Doe</p>
        <p><strong>ID:</strong> 123456</p>
        <p><strong>Gender:</strong> Male</p>
        <p><strong>Doctor Name:</strong> Dr. Jane Smith</p>
        <p><strong>Treatment Date:</strong> June 28, 2023</p>
        <div className="symptom-list">
          <h3>Symptoms:</h3>
          {symptoms.map((symptom, index) => (
            <div className="symptom-item" key={index}>
              <span>{symptom}</span>
              <button
                className="delete-button"
                onClick={() => handleDeleteSymptom(index)}
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  className="feather feather-x"
                >
                  <line x1="18" y1="6" x2="6" y2="18" />
                  <line x1="6" y1="6" x2="18" y2="18" />
                </svg>
              </button>
            </div>
          ))}
          {showSymptomList && (
            <div className="symptom-list-overlay">
              <SymptomList symptoms={symptoms} onAddSymptom={handleAddSymptom} />
            </div>
          )}
          <button
            className="add-symptom-button"
            onClick={() => setShowSymptomList(true)}
          >
            Add Symptom
          </button>
        </div>
        <p><strong>Treatment Progress:</strong> In progress</p>
      </div>
      <button className="confirm-button" onClick={handleConfirm}>Confirm</button>
    </div>
  );
}

export default ViewTreatmentProfile;
