import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Autosuggest from 'react-autosuggest';
import './AdminCreateSlot.css';

function CreateSchedulePage() {
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [selectedShifts, setSelectedShifts] = useState([]);
  const [isScheduleCreated, setIsScheduleCreated] = useState(false);
  const [searchValue, setSearchValue] = useState('');
  const [suggestedDoctors, setSuggestedDoctors] = useState([]);

  useEffect(() => {
    // Gửi yêu cầu GET tới API để lấy danh sách bác sĩ
    axios
      .get('http://localhost:3000/api/doctors')
      .then((response) => {
        const doctorList = response.data.doctors;
        setSuggestedDoctors(doctorList);
      })
      .catch((error) => {
        console.error(error);
        // Xử lý lỗi nếu có
      });
  }, []);

  const handleSelectDoctor = (doctor) => {
    setSelectedDoctor(doctor);
    setSearchValue('');
    setSuggestedDoctors([]);
  };

  const handleSelectStartDate = (date) => {
    setStartDate(date);
  };

  const handleSelectEndDate = (date) => {
    setEndDate(date);
  };

  const handleSelectShift = (shift) => {
    const updatedShifts = [...selectedShifts];
    if (updatedShifts.includes(shift)) {
      updatedShifts.splice(updatedShifts.indexOf(shift), 1);
    } else {
      updatedShifts.push(shift);
    }
    setSelectedShifts(updatedShifts);
  };

  const handleSaveSchedule = () => {
    if (selectedDoctor && startDate && endDate && selectedShifts.length > 0) {
      setIsScheduleCreated(true);
    } else {
      // Hiển thị thông báo lỗi hoặc yêu cầu người dùng nhập đầy đủ thông tin
    }
  };

  const handleSearchDoctor = (value) => {
    setSearchValue(value);
  };

  const getSuggestions = (value) => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    return inputLength === 0
      ? []
      : suggestedDoctors.filter(
          (doctor) => doctor.fullname.toLowerCase().slice(0, inputLength) === inputValue
        );
  };

  const getSuggestionValue = (suggestion) => suggestion.fullname;

  const renderSuggestion = (suggestion) => (
    <div>
      <img src={suggestion.avatar} alt={suggestion.fullname} />
      {suggestion.fullname}
    </div>
  );

  const onSuggestionSelected = (event, { suggestion }) => {
    handleSelectDoctor(suggestion);
  };

  const inputProps = {
    placeholder: 'Tìm kiếm bác sĩ',
    value: searchValue,
    onChange: (event, { newValue }) => handleSearchDoctor(newValue),
  };

  return (
    <div className="page-container">
      <div className="admin-create-slot__container">
        <h2>Tạo Lịch Làm Việc</h2>

        {/* Chọn bác sĩ */}
        <div>
          <h3>Chọn bác sĩ</h3>
          <Autosuggest
            suggestions={getSuggestions(searchValue)}
            onSuggestionsFetchRequested={() => {}}
            onSuggestionsClearRequested={() => {}}
            getSuggestionValue={getSuggestionValue}
            renderSuggestion={renderSuggestion}
            onSuggestionSelected={onSuggestionSelected}
            inputProps={inputProps}
          />
        </div>

        {/* Chọn khoảng thời gian */}
        <div>
          <h3>Chọn khoảng thời gian</h3>
          {/* ... */}
        </div>

        {/* Chọn ca làm việc */}
        <div className="admin-create-slot__shifts-container">
          <h3>Chọn ca làm việc</h3>
          {/* ... */}
        </div>

        {/* Lưu */}
        <div>
          <button className="admin-create-slot__save-btn" onClick={handleSaveSchedule}>
            Lưu
          </button>
        </div>

        {/* Hiển thị lịch đã tạo */}
        {isScheduleCreated && (
          <div className="admin-create-slot__schedule-info">
            <h3>Lịch làm việc đã tạo</h3>
            <p>Bác sĩ: {selectedDoctor.fullname}</p>
            {/* ... */}
          </div>
        )}

        {/* Bảng */}
        <div className="admin-create-slot__table-wrapper">
          <table className="admin-create-slot__table">
            <thead>
              <tr>
                <th>Bác sĩ</th>
                {/* ... */}
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{selectedDoctor && selectedDoctor.fullname}</td>
                {/* ... */}
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default CreateSchedulePage;
