import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const bookingsPerPage = 5;

    // Dữ liệu mẫu
    const initialBookingList = [
        {
            id: 1,
            avatar: 'avatar_url_1',
            service: 'Khám tổng quát',
            status: 'Đã khám',
            date: '2023-07-01',
        },
        {
            id: 2,
            avatar: 'avatar_url_2',
            service: 'Lấy men răng',
            status: 'Đang chờ khám',
            date: '2023-07-01',
        },
        {
            id: 3,
            avatar: 'avatar_url_3',
            service: 'Nhổ răng khôn',
            status: 'Đang chờ khám',
            date: '2023-07-01',
        },
        {
            id: 4,
            avatar: 'avatar_url_4',
            service: 'Lấy tủy',
            status: 'Đang chờ khám',
            date: '2023-07-02',
        },
        {
            id: 5,
            avatar: 'avatar_url_5',
            service: 'Cạo vôi răng',
            status: 'Đang chờ khám',
            date: '2023-07-03',
        },
        {
            id: 6,
            avatar: 'avatar_url_6',
            service: 'Trám răng',
            status: 'Đã khám',
            date: '2023-07-05',
        },
        {
            id: 7,
            avatar: 'avatar_url_7',
            service: 'Tẩy trắng răng',
            status: 'Đã khám',
            date: '2023-07-05',
        },
        {
            id: 8,
            avatar: 'avatar_url_8',
            service: 'Chỉnh nha',
            status: 'Đang chờ khám',
            date: '2023-07-06',
        },
        {
            id: 9,
            avatar: 'avatar_url_9',
            service: 'Niềng răng',
            status: 'Đang chờ khám',
            date: '2023-07-06',
        },
        {
            id: 10,
            avatar: 'avatar_url_10',
            service: 'Trồng răng',
            status: 'Đã khám',
            date: '2023-07-07',
        },
        {
            id: 11,
            avatar: 'avatar_url_11',
            service: 'Tạo hình răng',
            status: 'Đã khám',
            date: '2023-07-07',
        },
        {
            id: 12,
            avatar: 'avatar_url_12',
            service: 'Chỉnh hình răng',
            status: 'Đang chờ khám',
            date: '2023-07-08',
        },
        {
            id: 13,
            avatar: 'avatar_url_13',
            service: 'Trám hình răng',
            status: 'Đang chờ khám',
            date: '2023-07-08',
        },
        {
            id: 14,
            avatar: 'avatar_url_14',
            service: 'Lấy cao răng',
            status: 'Đã khám',
            date: '2023-07-09',
        },
        {
            id: 15,
            avatar: 'avatar_url_15',
            service: 'Phục hình răng',
            status: 'Đã khám',
            date: '2023-07-09',
        },
    ];
    const futureBookings = [
        {
            id: 4,
            avatar: 'avatar_url_4',
            service: 'Lấy tủy',
            status: 'Đang chờ khám',
            date: '2023-07-02',
        },
        {
            id: 5,
            avatar: 'avatar_url_5',
            service: 'Cạo vôi răng',
            status: 'Đang chờ khám',
            date: '2023-07-03',
        },
        // Thêm các dữ liệu mẫu khác
    ];

    const [startBookingIndex, setStartBookingIndex] = useState(0);
    const [endBookingIndex, setEndBookingIndex] = useState(bookingsPerPage);

    const cancelBooking = (bookingId) => {
        // Logic hủy phiếu đặt lịch
    };

    const requestCancellation = (bookingId) => {
        // Logic gửi yêu cầu hủy phiếu đặt lịch
    };

    const renderBookings = () => {
        const currentBookings = initialBookingList.slice(startBookingIndex, endBookingIndex);

        return currentBookings.map((booking) => (
            <div className="booking-item" key={booking.id}>
                <img src={booking.avatar} alt="Avatar" className="avatar" />
                <p className="service">{booking.service}</p>
                <p className="status">{booking.status}</p>
                <p className="date">{booking.date}</p>
                <button className="view-button">Xem phiếu</button>
                {booking.status === 'Đang chờ khám' && (
                    <button className="cancel-button" onClick={() => requestCancellation(booking.id)}>
                        Gửi yêu cầu hủy
                    </button>
                )}
            </div>
        ));
    };

    const renderSchedule = () => {
        const currentBookings = futureBookings.slice(startBookingIndex, endBookingIndex);

        return currentBookings.map((booking) => (
            <div className="booking-item" key={booking.id}>
                <img src={booking.avatar} alt="Avatar" className="avatar" />
                <p className="service">{booking.service}</p>
                <p className="status">{booking.status}</p>
                <p className="date">{booking.date}</p>
                <button className="view-button">Xem lịch trình</button>
                {booking.status === 'Đang chờ khám' && (
                    <button className="cancel-button" onClick={() => cancelBooking(booking.id)}>
                        Hủy phiếu
                    </button>
                )}
            </div>
        ));
    };

    const renderPaginationItems = (totalPages) => {
        const paginationItems = [];

        for (let i = 1; i <= totalPages; i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === currentPage ? 'active' : ''}`}
                    onClick={() => {
                        setCurrentPage(i);
                        setStartBookingIndex((i - 1) * bookingsPerPage);
                        setEndBookingIndex(i * bookingsPerPage);
                    }}
                >
                    {i}
                </button>
            );
        }

        return paginationItems;
    };

    const getCurrentDate = () => {
        const currentDate = new Date();
        currentDate.setHours(0, 0, 0, 0);
        return currentDate.toISOString().split('T')[0];
    };

    return (
        <div className="view-booking">
            <h1>Phiếu đặt lịch khám</h1>
            <div className="booking-list">{renderBookings()}</div>
            <div className="pagination">{renderPaginationItems(Math.ceil(initialBookingList.length / bookingsPerPage))}</div>

            <h2>Lịch trình khám</h2>
            <div className="booking-list">{renderSchedule()}</div>
            <div className="pagination">{renderPaginationItems(Math.ceil(futureBookings.length / bookingsPerPage))}</div>
        </div>
    );
};

export default ViewBooking;
