import React from 'react';
import './ViewTreatmentProfile.css';
import SymptomList from './SymptomList/SymptomList';

function ViewTreatmentProfile() {
  // Function to handle adding a symptom to the profile
  const handleAddSymptom = (symptom) => {
    console.log('Add Symptom:', symptom);
    // Logic to add the symptom to the profile
    // ...
  };

  // Function to handle deleting a symptom from the profile
  const handleDeleteSymptom = (symptom) => {
    console.log('Delete Symptom:', symptom);
    // Logic to delete the symptom from the profile
    // ...
  };

  // Profile details
  const profile = {
    name: 'John Doe',
    id: '123456',
    gender: 'Male',
    doctorName: 'Dr. Jane Smith',
    treatmentDate: 'June 28, 2023',
    symptoms: ['Fever', 'Headache', 'Cough'],
    treatmentProgress: 'In progress'
  };

  // Function to handle confirming the profile
  const handleConfirm = () => {
    alert('Profile confirmed');
    // Logic to confirm the profile
    // ...
  };

  return (
    <div className="profile-container">
      <h2>View Profile</h2>
      <div className="profile-details">
        <p><strong>Name:</strong> {profile.name}</p>
        <p><strong>ID:</strong> {profile.id}</p>
        <p><strong>Gender:</strong> {profile.gender}</p>
        <p><strong>Doctor Name:</strong> {profile.doctorName}</p>
        <p><strong>Treatment Date:</strong> {profile.treatmentDate}</p>
        <div className="symptom-list">
          <h3>Symptoms:</h3>
          {/* Display each symptom as a list item */}
          {profile.symptoms.map((symptom, index) => (
            <div className="symptom-item" key={index}>
              <span>{symptom}</span>
              {/* Button to delete the symptom */}
              <button
                className="delete-button"
                onClick={() => handleDeleteSymptom(symptom)}
              >
                X
              </button>
            </div>
          ))}
          {/* SymptomList component for adding new symptoms */}
          <SymptomList symptoms={profile.symptoms} onAddSymptom={handleAddSymptom} />
        </div>
        <p><strong>Treatment Progress:</strong> {profile.treatmentProgress}</p>
      </div>
      <button className="confirm-button" onClick={handleConfirm}>Confirm</button>
    </div>
  );
}

export default ViewTreatmentProfile;
