import React, { useState } from 'react';
import './DoctorViewProfile.css';

function DoctorViewProfile() {
    const [workday, setWorkday] = useState({
        day: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString().slice(0, 10),
        timeSlots: [
            { time: '7-8h', selected: false },
            { time: '8-9h', selected: false },
            { time: '9-10h', selected: false },
            { time: '10-11h', selected: false },
            { time: '14-15h', selected: false },
            { time: '15-16h', selected: false },
            { time: '16-17h', selected: false },
        ],
    });

    const handleTimeSlotClick = (index) => {
        const updatedTimeSlots = [...workday.timeSlots];
        updatedTimeSlots[index].selected = !updatedTimeSlots[index].selected;
        setWorkday({ ...workday, timeSlots: updatedTimeSlots });
    };

    return (
        <div className="doctor-profile-container">
            <div className="doctor-profile__info">
                <div className="doctor-profile__avatar">
                    <img src={doctor.avatar} alt="Avatar" />
                </div>
                <div className="doctor-profile__details">
                    <h2>{doctor.name}</h2>
                    <p>CCCD: {doctor.cccd}</p>
                    <p>Ngày sinh: {doctor.birthdate}</p>
                    <p>Số điện thoại: {doctor.phone}</p>
                    <p>Giới tính: {doctor.gender}</p>
                    <p>Email: {doctor.email}</p>
                    <p>Địa chỉ: {doctor.address}</p>
                    <p>Bằng cấp: {doctor.qualifications}</p>
                    <p>Kinh nghiệm: {doctor.experience}</p>
                </div>
            </div>

            <div className="doctor-profile__schedule">
                <h3>Lịch đặt khám</h3>
                <p>Ngày: {workday.day}</p>
                <div className="doctor-profile__timeslots">
                    {workday.timeSlots.map((slot, index) => (
                        <div
                            key={index}
                            className={`doctor-profile__timeslot ${slot.selected ? 'selected' : ''}`}
                            onClick={() => handleTimeSlotClick(index)}
                        >
                            {slot.time}
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default DoctorViewProfile;
