import React, { useState } from 'react';
import './SymptomList.css';

function SymptomList({ symptoms, onAddSymptom }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [newSymptom, setNewSymptom] = useState('');
  const [newSymptomDescription, setNewSymptomDescription] = useState('');
  const [showModal, setShowModal] = useState(false);
  const exampleSymptoms = ['Fever', 'Headache', 'Cough']; // Example symptom list

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleAddNewSymptom = () => {
    setShowModal(true);
  };

  const handleSaveSymptom = () => {
    const symptom = {
      name: newSymptom,
      description: newSymptomDescription
    };

    // Call the onAddSymptom callback to add the new symptom to the profile
    onAddSymptom(symptom);

    // Reset the input fields and close the modal
    setNewSymptom('');
    setNewSymptomDescription('');
    setShowModal(false);
  };

  return (
    <div className="symptom-list-container">
      <div className="search-box">
        <input
          type="text"
          placeholder="Search symptoms..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <div className="search-results">
        {exampleSymptoms.map((symptom) => (
          <div className="symptom-item" key={symptom}>
            {symptom}
          </div>
        ))}
        {showModal && (
          <div className="create-symptom-modal">
            <h3>Create New Symptom</h3>
            <input
              type="text"
              placeholder="Symptom name"
              value={newSymptom}
              onChange={(event) => setNewSymptom(event.target.value)}
            />
            <textarea
              placeholder="Symptom description"
              value={newSymptomDescription}
              onChange={(event) => setNewSymptomDescription(event.target.value)}
            />
            <button className="save-symptom-button" onClick={handleSaveSymptom}>
              Save Symptom
            </button>
          </div>
        )}
      </div>
      <div className="add-symptom-section">
        <button className="add-symptom-button" onClick={handleAddNewSymptom}>
          Add Symptom
        </button>
      </div>
    </div>
  );
}

export default SymptomList;
