import React, { useState } from 'react';

function ViewBooking() {
    const [bookings, setBookings] = useState([
        {
            id: 1,
            customerName: 'Nguyễn Văn B',
            avatar: 'avatar.jpg',
            service: 'Khám tổng quát',
            status: 'Đã khám',
            date: '2023-05-26',
        },
        {
            id: 2,
            customerName: 'Trần Thị C',
            avatar: 'avatar.jpg',
            service: 'Lấy men răng',
            status: 'Đang chờ khám',
            date: '2023-05-26',
        },
        {
            id: 3,
            customerName: 'Phạm Văn D',
            avatar: 'avatar.jpg',
            service: 'Nhổ răng khôn',
            status: 'Đã hủy đang chờ',
            date: '2023-05-26',
        },
        // Add more bookings here...
    ]);

    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;
    const totalPages = Math.ceil(bookings.length / itemsPerPage);

    const handleCancelBooking = (id) => {
        setBookings((prevBookings) => {
            const updatedBookings = prevBookings.map((booking) =>
                booking.id === id ? { ...booking, status: 'Đã hủy' } : booking
            );
            return updatedBookings;
        });
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    const filteredBookings = bookings.filter((booking) => booking.date === '2023-05-26'); // Filter bookings for the current date

    const currentBookings = filteredBookings.slice(
        (currentPage - 1) * itemsPerPage,
        currentPage * itemsPerPage
    );

    return (
        <div>
            <h2>Danh sách phiếu đặt lịch khám:</h2>
            <h3>Hôm nay, Ngày 26 Tháng 5 Năm 2023</h3>
            {currentBookings.length > 0 ? (
                <div>
                    {currentBookings.map((booking) => (
                        <div key={booking.id}>
                            <img src={booking.avatar} alt="Avatar" />
                            <div>{booking.service}</div>
                            <div>{booking.status}</div>
                            <button>Xem phiếu khám</button>
                            {booking.status === 'Đã khám' ? (
                                <button>Hoàn thành</button>
                            ) : booking.status === 'Đang chờ khám' ? (
                                <button>Gửi đơn hủy yêu cầu</button>
                            ) : booking.status === 'Đã hủy đang chờ' ? (
                                <button>Đã hủy đơn</button>
                            ) : (
                                <button>Đang chờ hủy đơn</button>
                            )}
                        </div>
                    ))}
                    <div>
                        {Array.from({ length: totalPages }, (_, index) => (
                            <button key={index + 1} onClick={() => handlePageChange(index + 1)}>
                                {index + 1}
                            </button>
                        ))}
                    </div>
                </div>
            ) : (
                <div>Không có phiếu đặt lịch khám cho hôm nay.</div>
            )}
            <h2>Lịch trình khám:</h2>
            {filteredBookings.length > 0 ? (
                <div>
                    {filteredBookings.map((booking) => (
                        <div key={booking.id}>
                            <img src={booking.avatar} alt="Avatar" />
                            <div>{booking.service}</div>
                            <div>{booking.status}</div>
                            <div>Ngày hẹn: {booking.date}</div>
                            <button>Xem phiếu khám</button>
                            {booking.status === 'Đã nhận' ? (
                                <button onClick={() => handleCancelBooking(booking.id)}>Hủy</button>
                            ) : (
                                <button>Đã hủy</button>
                            )}
                        </div>
                    ))}
                </div>
            ) : (
                <div>Không có lịch trình khám.</div>
            )}
        </div>
    );
}

export default ViewBooking;
