import React, { useState } from 'react';
import './ViewTreatmentProfile.css'; // Import the CSS file for styling

function ViewTreatmentProfile() {
  const profile = {
    name: 'John Doe',
    id: '123456',
    gender: 'Male',
    doctorName: 'Dr. Jane Smith',
    treatmentDate: 'June 28, 2023',
    symptoms: ['Fever', 'Headache', 'Cough'],
    treatmentProgress: 'In progress'
  };

  const [searchTerm, setSearchTerm] = useState('');
  const [symptoms, setSymptoms] = useState([
    'Fever',
    'Headache',
    'Cough',
    'Fatigue',
    'Joint pain',
    'Nausea'
  ]);
  const [newSymptom, setNewSymptom] = useState('');
  const [showNewSymptomModal, setShowNewSymptomModal] = useState(false);

  const handleConfirm = () => {
    // Handle the confirmation logic here
    alert('Profile confirmed');
  };

  const handleAddSymptom = (symptom) => {
    // Handle adding a symptom to the profile
    const updatedSymptoms = [...profile.symptoms, symptom];
    profile.symptoms = updatedSymptoms;
    console.log('Added Symptom:', symptom);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleNewSymptomInputChange = (event) => {
    setNewSymptom(event.target.value);
  };

  const handleCreateNewSymptom = () => {
    if (newSymptom) {
      setSymptoms([...symptoms, newSymptom]);
      setNewSymptom('');
      setShowNewSymptomModal(false);
    }
  };

  const openNewSymptomModal = () => {
    setShowNewSymptomModal(true);
  };

  const closeNewSymptomModal = () => {
    setShowNewSymptomModal(false);
  };

  const filteredSymptoms = symptoms.filter((symptom) =>
    symptom.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="profile-container">
      <h2>View Profile</h2>
      <div className="profile-details">
        <p><strong>Name:</strong> {profile.name}</p>
        <p><strong>ID:</strong> {profile.id}</p>
        <p><strong>Gender:</strong> {profile.gender}</p>
        <p><strong>Doctor Name:</strong> {profile.doctorName}</p>
        <p><strong>Treatment Date:</strong> {profile.treatmentDate}</p>
        <div className="symptom-list">
          <h3>Symptoms:</h3>
          <ul>
            {profile.symptoms.map((symptom, index) => (
              <li key={index}>{symptom}</li>
            ))}
          </ul>
          <div className="add-symptom">
            <input
              type="text"
              placeholder="Search or create new symptom"
              value={searchTerm}
              onChange={handleSearch}
            />
            <button onClick={openNewSymptomModal}>Add</button>
          </div>
        </div>
        <p><strong>Treatment Progress:</strong> {profile.treatmentProgress}</p>
      </div>
      <button className="confirm-button" onClick={handleConfirm}>Confirm</button>

      {showNewSymptomModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeNewSymptomModal}>&times;</span>
            <h3>Create New Symptom</h3>
            <div className="new-symptom-form">
              <label>Symptom Name:</label>
              <input
                type="text"
                value={newSymptom}
                onChange={handleNewSymptomInputChange}
              />
              <button onClick={handleCreateNewSymptom}>Create</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ViewTreatmentProfile;
