import React, { useState } from 'react';

function ViewBooking() {
    const [bookings, setBookings] = useState([
        {
            id: 1,
            customerName: 'John Doe',
            service: 'Khám tổng quát',
            status: 'Đang chờ',
        },
        {
            id: 2,
            customerName: 'Jane Smith',
            service: 'Lấy men răng',
            status: 'Đã khám',
        },
    ]);

    const cancelBooking = (bookingId) => {
        // Gửi yêu cầu hủy đơn đến admin (cập nhật qua API hoặc thông báo trong ứng dụng)
        // Sau khi gửi yêu cầu, cập nhật trạng thái của phiếu đặt lịch thành "Đang chờ hủy đơn"
        const updatedBookings = bookings.map((booking) => {
            if (booking.id === bookingId) {
                return { ...booking, status: 'Đang chờ hủy đơn' };
            }
            return booking;
        });
        setBookings(updatedBookings);
    };

    const acceptCancellation = (bookingId) => {
        // Xử lý chấp nhận yêu cầu hủy đơn từ admin
        // Cập nhật trạng thái của phiếu đặt lịch thành "Đã hủy"
        const updatedBookings = bookings.map((booking) => {
            if (booking.id === bookingId) {
                return { ...booking, status: 'Đã hủy' };
            }
            return booking;
        });
        setBookings(updatedBookings);
    };

    return (
        <div>
            <h2>Danh sách phiếu đặt lịch khám</h2>
            <h3>Hôm nay, Ngày 26 Tháng 5 Năm 2023</h3>
            {bookings.map((booking) => (
                <div key={booking.id}>
                    <div>Avatar của khách hàng: {booking.customerName}</div>
                    <div>Dịch vụ: {booking.service}</div>
                    <div>Trạng thái: {booking.status}</div>
                    {booking.status === 'Đã khám' ? (
                        <button disabled>Xem phiếu khám</button>
                    ) : (
                        <button>Gửi yêu cầu hủy đơn</button>
                    )}
                    {booking.status === 'Đang chờ hủy đơn' && (
                        <button onClick={() => acceptCancellation(booking.id)}>Chấp nhận hủy đơn</button>
                    )}
                </div>
            ))}
            <h3>Lịch trình khám</h3>
            {/* Hiển thị danh sách phiếu đặt lịch với trạng thái "Đã nhận" và "Đã hủy" */}
        </div>
    );
}

export default ViewBooking;
