import React, { useState } from 'react';

function ViewBooking() {
    const [bookings, setBookings] = useState([
        {
            id: 1,
            customerName: 'John Doe',
            service: 'Khám tổng quát',
            status: 'Đã khám',
        },
        {
            id: 2,
            customerName: 'Jane Smith',
            service: 'Lấy men răng',
            status: 'Đang chờ khám',
        },
        {
            id: 3,
            customerName: 'Alice Johnson',
            service: 'Nhổ răng khôn',
            status: 'Đã hủy',
        },
        // Thêm các phiếu đặt lịch khác vào đây
    ]);

    const cancelBooking = (bookingId) => {
        // Gửi yêu cầu hủy đơn đến admin và cập nhật trạng thái thành "Đang chờ duyệt"
        const updatedBookings = bookings.map((booking) => {
            if (booking.id === bookingId && booking.status !== 'Đã khám' && booking.status !== 'Đã hủy') {
                return { ...booking, status: 'Đang chờ duyệt' };
            }
            return booking;
        });
        setBookings(updatedBookings);
    };

    const acceptCancellation = (bookingId) => {
        // Xử lý chấp nhận yêu cầu hủy đơn từ admin và cập nhật trạng thái thành "Đã hủy đơn"
        const updatedBookings = bookings.map((booking) => {
            if (booking.id === bookingId && booking.status === 'Đang chờ duyệt') {
                return { ...booking, status: 'Đã hủy đơn' };
            }
            return booking;
        });
        setBookings(updatedBookings);
    };

    return (
        <div>
            <h2>Danh sách phiếu đặt lịch khám</h2>
            <h3>Hôm nay, Ngày 26 Tháng 5 Năm 2023</h3>
            {bookings.map((booking) => (
                <div key={booking.id}>
                    <div>Avatar của khách hàng: {booking.customerName}</div>
                    <div>Dịch vụ: {booking.service}</div>
                    <div>Trạng thái: {booking.status}</div>
                    <button>Xem phiếu khám</button>
                    {booking.status !== 'Đã khám' && booking.status !== 'Đã hủy' && (
                        <button onClick={() => cancelBooking(booking.id)}>Gửi yêu cầu hủy đơn</button>
                    )}
                    {booking.status === 'Đang chờ duyệt' && (
                        <button onClick={() => acceptCancellation(booking.id)}>Chấp nhận hủy đơn</button>
                    )}
                </div>
            ))}
        </div>
    );
}

export default ViewBooking;
