import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
  const [selectedBooking, setSelectedBooking] = useState(null);
  const currentDate = new Date().toLocaleDateString('vi-VN', {
    day: 'numeric',
    month: 'long',
    year: 'numeric',
  });

  const bookingList = [
    {
      id: 1,
      patientName: 'Nguyễn Văn A',
      reason: 'Đau bụng',
      date: '02/07/2023',
      time: '09:00',
    },
    {
      id: 2,
      patientName: 'Trần Thị B',
      reason: 'Sốt cao',
      date: '03/07/2023',
      time: '10:30',
    },
    {
      id: 3,
      patientName: 'Lê Văn C',
      reason: 'Đau đầu',
      date: '05/07/2023',
      time: '14:00',
    },
    {
      id: 4,
      patientName: 'Phạm Thị D',
      reason: 'Ho khan',
      date: '06/07/2023',
      time: '11:30',
    },
    {
      id: 5,
      patientName: 'Nguyễn Văn E',
      reason: 'Mệt mỏi',
      date: '08/07/2023',
      time: '16:15',
    },
    {
      id: 6,
      patientName: 'Trần Thị F',
      reason: 'Đau khớp',
      date: '10/07/2023',
      time: '10:00',
    },
    {
      id: 7,
      patientName: 'Lê Văn G',
      reason: 'Chóng mặt',
      date: '12/07/2023',
      time: '15:30',
    },
    {
      id: 8,
      patientName: 'Phạm Thị H',
      reason: 'Sổ mũi',
      date: '14/07/2023',
      time: '09:45',
    },
    {
      id: 9,
      patientName: 'Nguyễn Văn I',
      reason: 'Mất ngủ',
      date: '16/07/2023',
      time: '13:30',
    },
    {
      id: 10,
      patientName: 'Trần Thị J',
      reason: 'Đau răng',
      date: '18/07/2023',
      time: '11:00',
    },
    {
      id: 11,
      patientName: 'Lê Văn K',
      reason: 'Buồn nôn',
      date: '20/07/2023',
      time: '14:45',
    },
    {
      id: 12,
      patientName: 'Phạm Thị L',
      reason: 'Đau cổ',
      date: '22/07/2023',
      time: '10:15',
    },
    {
      id: 13,
      patientName: 'Nguyễn Văn M',
      reason: 'Sưng mắt',
      date: '24/07/2023',
      time: '15:00',
    },
    {
      id: 14,
      patientName: 'Trần Thị N',
      reason: 'Sỏi thận',
      date: '26/07/2023',
      time: '09:30',
    },
    {
      id: 15,
      patientName: 'Lê Văn O',
      reason: 'Tiểu đêm',
      date: '28/07/2023',
      time: '12:45',
    },
    {
      id: 16,
      patientName: 'Phạm Thị P',
      reason: 'Mất khẩu phần',
      date: '30/07/2023',
      time: '14:30',
    },
    {
      id: 17,
      patientName: 'Nguyễn Văn Q',
      reason: 'Đau tim',
      date: '01/08/2023',
      time: '11:45',
    },
    {
      id: 18,
      patientName: 'Trần Thị R',
      reason: 'Nổi mẩn',
      date: '03/08/2023',
      time: '16:00',
    },
    {
      id: 19,
      patientName: 'Lê Văn S',
      reason: 'Ho khan',
      date: '05/08/2023',
      time: '10:30',
    },
    {
      id: 20,
      patientName: 'Phạm Thị T',
      reason: 'Mệt mỏi',
      date: '07/08/2023',
      time: '13:15',
    },
  ];

  const viewMedicalRecord = (bookingId) => {
    const selected = bookingList.find((booking) => booking.id === bookingId);
    setSelectedBooking(selected);
  };

  const renderMedicalRecordPopup = () => {
    if (selectedBooking) {
      return (
        <div className="medical-record-popup">
          <h4>Thông tin phiếu khám</h4>
          <div>
            <strong>Họ tên bệnh nhân:</strong> {selectedBooking.patientName}
          </div>
          <div>
            <strong>Lý do đến khám:</strong> {selectedBooking.reason}
          </div>
          <div>
            <strong>Ngày khám:</strong> {selectedBooking.date}
          </div>
          <div>
            <strong>Giờ khám:</strong> {selectedBooking.time}
          </div>
          <button onClick={() => setSelectedBooking(null)}>Đóng</button>
        </div>
      );
    }
  };

  return (
    <div className="booking-container">
      <h2>Lịch khám hôm nay {currentDate}</h2>
      {/* Hiển thị lịch khám hôm nay */}

      <h2>Lịch khám trong tương lai</h2>
      <ul>
        {bookingList.map((booking) => (
          <li className="booking-item" key={booking.id}>
            <div>
              <strong>Họ tên bệnh nhân:</strong> {booking.patientName}
            </div>
            <div>
              <strong>Lý do đến khám:</strong> {booking.reason}
            </div>
            <div>
              <strong>Ngày khám:</strong> {booking.date}
            </div>
            <div>
              <strong>Giờ khám:</strong> {booking.time}
            </div>
            <button onClick={() => viewMedicalRecord(booking.id)}>
              Xem chi tiết
            </button>
          </li>
        ))}
      </ul>

      {renderMedicalRecordPopup()}
    </div>
  );
};

export default ViewBooking;
