import React from 'react';

const Pagination = ({
  currentPage,
  totalPages,
  onPageChange,
  pageRangeDisplayed,
}) => {
  const getPageNumbers = () => {
    const pageNumbers = [];
    const range = Math.min(pageRangeDisplayed, totalPages);

    let startPage = Math.max(1, currentPage - Math.floor(range / 2));
    let endPage = startPage + range - 1;

    if (endPage > totalPages) {
      endPage = totalPages;
      startPage = Math.max(1, endPage - range + 1);
    }

    for (let i = startPage; i <= endPage; i++) {
      pageNumbers.push(i);
    }

    return pageNumbers;
  };

  const handlePageChange = (page) => {
    if (currentPage !== page && page >= 1 && page <= totalPages) {
      onPageChange(page);
    }
  };

  const pageNumbers = getPageNumbers();

  return (
    <ul className="pagination">
      {pageNumbers.map((page) => (
        <li
          key={page}
          className={currentPage === page ? 'active' : ''}
          onClick={() => handlePageChange(page)}
        >
          {page}
        </li>
      ))}
    </ul>
  );
};

export default Pagination;
