import React, { useState } from 'react';
import './CreateTreatementIn.css'; // Import the CSS file for styling

function CreateTreatementIn() {
  const [treatmentProgress, setTreatmentProgress] = useState('');
  const [result, setResult] = useState('');
  const [advice, setAdvice] = useState('');
  const [treatmentProfiles, setTreatmentProfiles] = useState([]);

  const handleAddLine = () => {
    setTreatmentProfiles((prevProfiles) => [
      ...prevProfiles,
      { treatmentProgress: '', result: '', advice: '' }
    ]);
  };

  const handleComplete = () => {
    // Handle logic for completing the treatment
    console.log('Treatment Complete');
  };

  const handleReappoint = () => {
    // Handle logic for re-appointing the treatment
    console.log('Re-appoint Treatment');
  };

  const handleProfileChange = (index, field, value) => {
    setTreatmentProfiles((prevProfiles) => {
      const updatedProfiles = [...prevProfiles];
      updatedProfiles[index][field] = value;
      return updatedProfiles;
    });
  };

  return (
    <div className="view-profile-page">
      <h2>View Profile</h2>
      <div className="profile-details">
        <p><strong>Name:</strong> John Doe</p>
        <p><strong>ID:</strong> 123456</p>
        <p><strong>Gender:</strong> Male</p>
        <p><strong>Treatment Date:</strong> 2023-06-28</p>
      </div>
      <div className="input-form">
        <div className="treatment-profiles">
          {treatmentProfiles.map((profile, index) => (
            <div className="profile-line" key={index}>
              <input
                type="text"
                value={profile.treatmentProgress}
                onChange={(e) => handleProfileChange(index, 'treatmentProgress', e.target.value)}
                placeholder="Treatment Progress"
              />
              <input
                type="text"
                value={profile.result}
                onChange={(e) => handleProfileChange(index, 'result', e.target.value)}
                placeholder="Result"
              />
              <input
                type="text"
                value={profile.advice}
                onChange={(e) => handleProfileChange(index, 'advice', e.target.value)}
                placeholder="Advice"
              />
            </div>
          ))}
        </div>
        <button className="add-line-button" onClick={handleAddLine}>
          Add Line
        </button>
      </div>
      <div className="action-buttons">
        <button className="complete-button" onClick={handleComplete}>
          Complete
        </button>
        <button className="reappoint-button" onClick={handleReappoint}>
          Re-appoint
        </button>
      </div>
    </div>
  );
};

export default CreateTreatementIn;
