import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
  const [selectedBooking, setSelectedBooking] = useState(null);
  const currentDate = new Date().toLocaleDateString('vi-VN', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
  });

  const bookingList = [
    {
      id: 1,
      patientName: 'Nguyễn Văn A',
      reason: 'Đau bụng',
      date: currentDate,
      time: '08:00',
    },
    {
      id: 2,
      patientName: 'Trần Thị B',
      reason: 'Sốt cao',
      date: currentDate,
      time: '09:30',
    },
    {
      id: 3,
      patientName: 'Lê Văn C',
      reason: 'Đau họng',
      date: currentDate,
      time: '11:15',
    },
    {
      id: 4,
      patientName: 'Phạm Thị D',
      reason: 'Mất ngủ',
      date: currentDate,
      time: '14:00',
    },
    {
      id: 5,
      patientName: 'Nguyễn Thanh E',
      reason: 'Đau lưng',
      date: currentDate,
      time: '15:45',
    },
    {
      id: 6,
      patientName: 'Trần Văn F',
      reason: 'Sổ mũi',
      date: '02/07/2023',
      time: '10:30',
    },
    {
      id: 7,
      patientName: 'Lê Thị G',
      reason: 'Đau răng',
      date: '02/07/2023',
      time: '12:45',
    },
    {
      id: 8,
      patientName: 'Nguyễn Văn H',
      reason: 'Sưng nước mắt',
      date: '02/07/2023',
      time: '16:30',
    },
    {
      id: 9,
      patientName: 'Phạm Thị I',
      reason: 'Đau khớp',
      date: '02/07/2023',
      time: '17:45',
    },
    {
      id: 10,
      patientName: 'Trần Văn J',
      reason: 'Buồn nôn',
      date: '02/07/2023',
      time: '18:30',
    },
    {
      id: 11,
      patientName: 'Hoàng Thị K',
      reason: 'Mệt mỏi',
      date: '03/07/2023',
      time: '09:00',
    },
    {
      id: 12,
      patientName: 'Vũ Văn L',
      reason: 'Chảy máu chân',
      date: '03/07/2023',
      time: '11:30',
    },
    {
      id: 13,
      patientName: 'Nguyễn Thị M',
      reason: 'Đau đầu',
      date: '03/07/2023',
      time: '13:45',
    },
    {
      id: 14,
      patientName: 'Trần Văn N',
      reason: 'Sổ mũi',
      date: '03/07/2023',
      time: '16:15',
    },
    {
      id: 15,
      patientName: 'Lê Thị O',
      reason: 'Mất ngủ',
      date: '03/07/2023',
      time: '18:30',
    },
    {
      id: 16,
      patientName: 'Nguyễn Văn P',
      reason: 'Đau cổ',
      date: '04/07/2023',
      time: '10:30',
    },
    {
      id: 17,
      patientName: 'Trần Thị Q',
      reason: 'Đau bụng',
      date: '04/07/2023',
      time: '12:45',
    },
    {
      id: 18,
      patientName: 'Lê Văn R',
      reason: 'Đau răng',
      date: '04/07/2023',
      time: '15:00',
    },
    {
      id: 19,
      patientName: 'Phạm Thị S',
      reason: 'Sưng mắt',
      date: '04/07/2023',
      time: '17:15',
    },
    {
      id: 20,
      patientName: 'Nguyễn Thanh T',
      reason: 'Đau lưng',
      date: '04/07/2023',
      time: '19:30',
    },
  ];

  const handleViewBooking = (booking) => {
    setSelectedBooking(booking);
  };

  return (
    <div className="view-booking">
      <h2 className="section-title">Lịch khám hôm nay ({currentDate})</h2>
      <ul className="booking-list">
        {bookingList
          .filter((booking) => booking.date === currentDate)
          .map((booking) => (
            <li className="booking-item" key={booking.id}>
              <button className="booking-button" onClick={() => handleViewBooking(booking)}>Xem phiếu khám</button>
              <div className="booking-info">
                <span className="info-label">Họ tên:</span>
                <span className="info-value">{booking.patientName}</span>
                <span className="info-label">Lí do đến khám:</span>
                <span className="info-value">{booking.reason}</span>
                <span className="info-label">Giờ khám:</span>
                <span className="info-value">{booking.time}</span>
              </div>
            </li>
          ))}
      </ul>

      <h2 className="section-title">Lịch khám trong tương lai</h2>
      <ul className="booking-list">
        {bookingList
          .filter((booking) => booking.date !== currentDate)
          .map((booking) => (
            <li className="booking-item" key={booking.id}>
              <button className="booking-button" onClick={() => handleViewBooking(booking)}>Xem phiếu khám</button>
              <div className="booking-info">
                <span className="info-label">Họ tên:</span>
                <span className="info-value">{booking.patientName}</span>
                <span className="info-label">Lí do đến khám:</span>
                <span className="info-value">{booking.reason}</span>
                <span className="info-label">Ngày khám:</span>
                <span className="info-value">{booking.date}</span>
                <span className="info-label">Giờ khám:</span>
                <span className="info-value">{booking.time}</span>
              </div>
            </li>
          ))}
      </ul>

      {selectedBooking && (
        <div className="selected-booking">
          <h2 className="section-title">Thông tin phiếu khám</h2>
          <div className="booking-info">
            <span className="info-label">Họ tên:</span>
            <span className="info-value">{selectedBooking.patientName}</span>
            <span className="info-label">Lí do đến khám:</span>
            <span className="info-value">{selectedBooking.reason}</span>
            <span className="info-label">Ngày khám:</span>
            <span className="info-value">{selectedBooking.date}</span>
            <span className="info-label">Giờ khám:</span>
            <span className="info-value">{selectedBooking.time}</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default ViewBooking;
