import React, { useState } from 'react';
import './SymptomList.css';

function SymptomList({ onAddSymptom }) {
  const [symptoms, setSymptoms] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newSymptom, setNewSymptom] = useState('');
  const [newSymptomDescription, setNewSymptomDescription] = useState('');

  const handleAddSymptom = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSaveSymptom = () => {
    const symptom = {
      name: newSymptom,
      description: newSymptomDescription,
    };

    setSymptoms([...symptoms, symptom]);
    setNewSymptom('');
    setNewSymptomDescription('');
    setShowModal(false);
    onAddSymptom(symptom);
  };

  return (
    <div className="symptom-list">
      {/* <h3>Chỉnh sửa triệu chứng</h3> */}
      <ul>
        {symptoms.map((symptom, index) => (
          <li key={index}>{symptom.name}</li>
        ))}
      </ul>
      <button className="add-symptom" onClick={handleAddSymptom}>
        Thêm triệu chứng
      </button>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={handleCloseModal}>
              &times;
            </span>
            <h3>Thêm triệu chứng</h3>
            <div className="new-symptom-form">
              <label htmlFor="symptom">Tên triệu chứng:</label>
              <input
                type="text"
                id="symptom"
                value={newSymptom}
                onChange={(e) => setNewSymptom(e.target.value)}
              />

              <label htmlFor="description">Mô tả:</label>
              <input
                type="text"
                id="description"
                value={newSymptomDescription}
                onChange={(e) => setNewSymptomDescription(e.target.value)}
              />

              <button onClick={handleSaveSymptom}>Lưu</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SymptomList;
