import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(Math.ceil(bookingList.length / 5));
  const currentDate = new Date().toLocaleDateString('vi-VN', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric',
  });

  const bookingList = [
    {
      id: 1,
      patientName: 'Nguyễn Văn A',
      reason: 'Đau bụng',
      date: currentDate,
      time: '08:00',
      status: 'Đang chờ',
    },
    // ...Thêm thông tin phiếu khám khác vào đây...
  ];

  const handleViewBooking = (booking) => {
    setSelectedBooking(booking);
  };

  const handleViewProfile = (booking) => {
    // Xử lý logic khi xem hồ sơ của phiếu khám
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => prevPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  const renderBookingList = () => {
    const startIndex = (currentPage - 1) * 5;
    const endIndex = startIndex + 5;
    const currentBookingList = bookingList.slice(startIndex, endIndex);

    return currentBookingList.map((booking) => (
      <li className="booking-item" key={booking.id}>
        <button className="booking-button" onClick={() => handleViewBooking(booking)}>
          Xem phiếu khám
        </button>
        <span className="booking-info">Họ tên: {booking.patientName}</span>
        <span className="booking-info">Lí do đến khám: {booking.reason}</span>
        <span className="booking-info">Ngày khám: {booking.date}</span>
        <span className="booking-info">Giờ khám: {booking.time}</span>
        <span className="booking-info">Trạng thái: {booking.status}</span>
        {booking.status === 'Đang chờ' && currentDate === booking.date && (
          <button className="view-profile-button" onClick={() => handleViewProfile(booking)}>
            Xem hồ sơ
          </button>
        )}
      </li>
    ));
  };

  const renderPagination = () => {
    const pageNumbers = [];
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }

    return (
      <div className="pagination">
        <button
          className="pagination-button"
          disabled={currentPage === 1}
          onClick={handlePreviousPage}
        >
          &#8249;
        </button>
        {pageNumbers.map((pageNumber) => (
          <button
            key={pageNumber}
            className={`pagination-button ${pageNumber === currentPage ? 'active' : ''}`}
            onClick={() => handlePageChange(pageNumber)}
          >
            {pageNumber}
          </button>
        ))}
        <button
          className="pagination-button"
          disabled={currentPage === totalPages}
          onClick={handleNextPage}
        >
          &#8250;
        </button>
      </div>
    );
  };

  return (
    <div className="view-booking-container">
      <h2 className="booking-heading">Lịch khám hôm nay ({currentDate})</h2>
      <ul className="booking-list">{renderBookingList()}</ul>
      {renderPagination()}

      {selectedBooking && (
        <div className="selected-booking">
          <h2 className="selected-booking-heading">Phiếu khám đang được xem:</h2>
          <span className="booking-info">Họ tên: {selectedBooking.patientName}</span>
          <span className="booking-info">Lí do đến khám: {selectedBooking.reason}</span>
          <span className="booking-info">Ngày khám: {selectedBooking.date}</span>
          <span className="booking-info">Giờ khám: {selectedBooking.time}</span>
          <span className="booking-info">Trạng thái: {selectedBooking.status}</span>
          <button className="close-profile-button" onClick={() => setSelectedBooking(null)}>
            Đóng
          </button>
        </div>
      )}
    </div>
  );
};

export default ViewBooking;
