import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
    const [currentBookingsPage, setCurrentBookingsPage] = useState(1);
    const [futureBookingsPage, setFutureBookingsPage] = useState(1);
    const bookingsPerPage = 5;

    const bookingList = [
        {
            id: 1,
            avatar: 'avatar_url_1',
            service: 'Khám tổng quát',
            status: 'Đã khám',
            date: '2023-07-01',
        },
        {
            id: 2,
            avatar: 'avatar_url_2',
            service: 'Chụp X-quang',
            status: 'Đã khám',
            date: '2023-07-02',
        },
        {
            id: 3,
            avatar: 'avatar_url_3',
            service: 'Răng sứ',
            status: 'Đã khám',
            date: '2023-07-03',
        },
        // Thêm dữ liệu mẫu cho các phiếu đặt lịch khác
    ];

    const currentBookings = [
        {
            id: 4,
            avatar: 'avatar_url_4',
            service: 'Lấy tủy',
            status: 'Đang chờ khám',
            date: '2023-07-02',
        },
        {
            id: 5,
            avatar: 'avatar_url_5',
            service: 'Chẩn đoán MRI',
            status: 'Đang chờ khám',
            date: '2023-07-04',
        },
        {
            id: 6,
            avatar: 'avatar_url_6',
            service: 'Nạo hút ung thư',
            status: 'Đang chờ khám',
            date: '2023-07-05',
        },
        // Thêm dữ liệu mẫu cho các phiếu đặt lịch hiện tại khác
    ];

    const cancelBooking = (bookingId) => {
        const updatedBookingList = bookingList.map((booking) =>
            booking.id === bookingId && booking.status !== 'Đã hủy'
                ? { ...booking, status: 'Đã hủy' }
                : booking
        );
        setBookingList(updatedBookingList); // Đã sửa thành setBookingList
    };

    const requestCancellation = (bookingId) => {
        const updatedCurrentBookings = currentBookings.map((booking) =>
            booking.id === bookingId
                ? { ...booking, status: 'Đang chờ hủy đơn' }
                : booking
        );
        setCurrentBookings(updatedCurrentBookings); // Đã sửa thành setCurrentBookings
    };

    const renderBookings = () => {
        const startIndex = (currentBookingsPage - 1) * bookingsPerPage;
        const endIndex = startIndex + bookingsPerPage;
        const bookingsToRender = currentBookings.slice(startIndex, endIndex);

        return bookingsToRender.map((booking) => (
            <div className="booking-item" key={booking.id}>
                <div>ID: {booking.id}</div>
                <div>Avatar: {booking.avatar}</div>
                <div>Service: {booking.service}</div>
                <div>Status: {booking.status}</div>
                <div>Date: {booking.date}</div>
                <button onClick={() => requestCancellation(booking.id)}>Request Cancellation</button>
            </div>
        ));
    };

    const renderFutureBookings = () => {
        const startIndex = (futureBookingsPage - 1) * bookingsPerPage;
        const endIndex = startIndex + bookingsPerPage;
        const futureBookingsToRender = bookingList.slice(startIndex, endIndex);

        return futureBookingsToRender.map((booking) => (
            <div className="booking-item" key={booking.id}>
                <div>ID: {booking.id}</div>
                <div>Avatar: {booking.avatar}</div>
                <div>Service: {booking.service}</div>
                <div>Status: {booking.status}</div>
                <div>Date: {booking.date}</div>
                <button onClick={() => cancelBooking(booking.id)}>Cancel Booking</button>
            </div>
        ));
    };

    const renderBookingsPagination = () => {
        const paginationItems = [];

        for (let i = 1; i <= Math.ceil(currentBookings.length / bookingsPerPage); i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === currentBookingsPage ? 'active' : ''}`}
                    onClick={() => setCurrentBookingsPage(i)}
                >
                    {i}
                </button>
            );
        }

        return (
            <div className="pagination">
                {paginationItems}
            </div>
        );
    };

    const renderFutureBookingsPagination = () => {
        const paginationItems = [];

        for (let i = 1; i <= Math.ceil(bookingList.length / bookingsPerPage); i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === futureBookingsPage ? 'active' : ''}`}
                    onClick={() => setFutureBookingsPage(i)}
                >
                    {i}
                </button>
            );
        }

        return (
            <div className="pagination">
                {paginationItems}
            </div>
        );
    };

    const getCurrentDate = () => {
        const currentDate = new Date();
        currentDate.setHours(0, 0, 0, 0);
        return currentDate.toISOString().split('T')[0];
    };

    return (
        <div className="view-booking">
            <h1>Danh sách phiếu đặt lịch khám</h1>
            <h2>Hiện tại, Ngày {getCurrentDate()}</h2>
            <div className="booking-list">{renderBookings()}</div>
            {renderBookingsPagination()}
            <div className="schedule">
                <h2>Tương lai</h2>
                <div className="booking-list">{renderFutureBookings()}</div>
                {renderFutureBookingsPagination()}
            </div>
        </div>
    );
};

export default ViewBooking;
