import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
    const [currentPage, setCurrentPage] = useState(1);
    const bookingsPerPage = 5;

    const bookingList = [
        {
            id: 1,
            avatar: 'avatar_url_1',
            service: 'Khám tổng quát',
            status: 'Đã khám',
            date: '2023-05-26',
        },
        {
            id: 2,
            avatar: 'avatar_url_2',
            service: 'Lấy men răng',
            status: 'Đang chờ khám',
            date: '2023-05-26',
        },
        {
            id: 3,
            avatar: 'avatar_url_3',
            service: 'Nhổ răng khôn',
            status: 'Đang chờ khám',
            date: '2023-05-26',
        },
        // ...Thêm dữ liệu phiếu đặt lịch khác tương tự
    ];

    const totalPages = Math.ceil(bookingList.length / bookingsPerPage);

    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const cancelBooking = (bookingId, date) => {
        const today = new Date().toISOString().split('T')[0];
        const updatedBookingList = bookingList.map((item) =>
            item.id === bookingId
                ? { ...item, status: date === today ? 'Đang chờ hủy đơn' : 'Đã hủy' }
                : item
        );
        // Cập nhật danh sách phiếu đặt lịch và các xử lý khác liên quan
        // ...
    };

    const renderBookings = () => {
        const startIndex = (currentPage - 1) * bookingsPerPage;
        const endIndex = startIndex + bookingsPerPage;
        const currentBookings = bookingList.slice(startIndex, endIndex);

        return currentBookings.map((booking) => (
            <div className="booking-item" key={booking.id}>
                <img src={booking.avatar} alt="Avatar" className="avatar" />
                <p className="service">{booking.service}</p>
                <p className="status">{booking.status}</p>
                <p className="date">{booking.date}</p>
                <button className="view-button">Xem phiếu</button>
                {booking.status === 'Đang chờ khám' && (
                    <button
                        className="cancel-button"
                        onClick={() => cancelBooking(booking.id, booking.date)}
                    >
                        {booking.date === new Date().toISOString().split('T')[0]
                            ? 'Gửi yêu cầu hủy'
                            : 'Hủy đơn'}
                    </button>
                )}
            </div>
        ));
    };

    const renderPaginationItems = () => {
        const paginationItems = [];

        for (let i = 1; i <= totalPages; i++) {
            paginationItems.push(
                <button
                    key={i}
                    className={`pagination-button ${i === currentPage ? 'active' : ''}`}
                    onClick={() => handlePageChange(i)}
                >
                    {i}
                </button>
            );
        }

        return paginationItems;
    };

    return (
        <div className="view-booking">
            <h1>Danh sách phiếu đặt lịch khám</h1>
            <h2>Hôm nay, Ngày 26 Tháng 5 Năm 2023</h2>
            <div className="booking-list">{renderBookings()}</div>
            <div className="pagination">{renderPaginationItems()}</div>
            <div className="schedule">
                <h2>Lịch trình khám</h2>
                <div className="booking-list">{renderBookings()}</div>
                <div className="pagination">{renderPaginationItems()}</div>
            </div>
        </div>
    );
};

export default ViewBooking;
