import React, { useState } from 'react';
import './ViewTreatmentProfile.css';
import SymptomList from './SymptomList/SymptomList';

function ViewTreatmentProfile() {
  const [symptoms, setSymptoms] = useState(['Fever', 'Headache', 'Cough']);
  const [showCreateSymptom, setShowCreateSymptom] = useState(false);

  const handleAddSymptom = (symptom) => {
    setSymptoms([...symptoms, symptom]);
  };

  const handleDeleteSymptom = (index) => {
    const updatedSymptoms = [...symptoms];
    updatedSymptoms.splice(index, 1);
    setSymptoms(updatedSymptoms);
  };

  const profile = {
    name: 'John Doe',
    id: '123456',
    gender: 'Male',
    doctorName: 'Dr. Jane Smith',
    treatmentDate: 'June 28, 2023',
    treatmentProgress: 'In progress'
  };

  const handleConfirm = () => {
    alert('Profile confirmed');
    // Logic to confirm the profile
    // ...
  };

  return (
    <div className="profile-container">
      <h2>View Profile</h2>
      <div className="profile-details">
        <p><strong>Name:</strong> {profile.name}</p>
        <p><strong>ID:</strong> {profile.id}</p>
        <p><strong>Gender:</strong> {profile.gender}</p>
        <p><strong>Doctor Name:</strong> {profile.doctorName}</p>
        <p><strong>Treatment Date:</strong> {profile.treatmentDate}</p>
        <div className="symptom-list">
          <h3>Symptoms:</h3>
          {symptoms.map((symptom, index) => (
            <div className="symptom-item" key={index}>
              <span>{symptom}</span>
              <button
                className="delete-button"
                onClick={() => handleDeleteSymptom(index)}
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  className="feather feather-x"
                >
                  <line x1="18" y1="6" x2="6" y2="18" />
                  <line x1="6" y1="6" x2="18" y2="18" />
                </svg>
              </button>
            </div>
          ))}
        </div>
        <p><strong>Treatment Progress:</strong> {profile.treatmentProgress}</p>
      </div>
      <button className="confirm-button" onClick={handleConfirm}>Confirm</button>

      {/* Conditionally render the Create New Symptom pop-up */}
      {showCreateSymptom && (
        <div className="create-symptom-popup">
          <h3>Create New Symptom</h3>
          {/* Add form fields for creating a new symptom */}
          {/* Example: */}
          <input type="text" placeholder="Symptom Name" />
          <textarea placeholder="Symptom Description" />
          <button>Save</button>
          <button onClick={() => setShowCreateSymptom(false)}>Cancel</button>
        </div>
      )}

      {/* Button to show the Create New Symptom pop-up */}
      <button className="add-symptom-button" onClick={() => setShowCreateSymptom(true)}>
        Create New Symptom
      </button>
    </div>
  );
}

export default ViewTreatmentProfile;
