import React, { useState } from 'react';
import './viewBooking.css';

const ViewBooking = () => {
    const [bookingList, setBookingList] = useState([
        {
            id: 1,
            avatar: 'avatar_url_1',
            service: 'Khám tổng quát',
            status: 'Đã khám',
            date: '2023-07-01',
        },
        {
            id: 2,
            avatar: 'avatar_url_2',
            service: 'Chụp X-quang',
            status: 'Đã khám',
            date: '2023-07-02',
        },
        {
            id: 3,
            avatar: 'avatar_url_3',
            service: 'Răng sứ',
            status: 'Đã khám',
            date: '2023-07-03',
        },
        // Thêm dữ liệu mẫu cho các phiếu đặt lịch khác
    ]);

    const [currentBookings, setCurrentBookings] = useState([
        {
            id: 4,
            avatar: 'avatar_url_4',
            service: 'Lấy tủy',
            status: 'Đang chờ khám',
            date: '2023-07-02',
        },
        {
            id: 5,
            avatar: 'avatar_url_5',
            service: 'Chẩn đoán MRI',
            status: 'Đang chờ khám',
            date: '2023-07-04',
        },
        {
            id: 6,
            avatar: 'avatar_url_6',
            service: 'Nạo hút ung thư',
            status: 'Đang chờ khám',
            date: '2023-07-05',
        },
        // Thêm dữ liệu mẫu cho các phiếu đặt lịch hiện tại khác
    ]);

    const cancelBooking = (bookingId) => {
        const updatedBookingList = bookingList.map((booking) =>
            booking.id === bookingId && booking.status !== 'Đã hủy'
                ? { ...booking, status: 'Đã hủy' }
                : booking
        );
        setBookingList(updatedBookingList);
    };

    const requestCancellation = (bookingId) => {
        const updatedCurrentBookings = currentBookings.map((booking) =>
            booking.id === bookingId
                ? { ...booking, status: 'Đang chờ hủy đơn' }
                : booking
        );
        setCurrentBookings(updatedCurrentBookings);
    };

    const renderBookings = (bookings) => {
        return bookings.map((booking) => (
            <div className="booking-item" key={booking.id}>
                <div>ID: {booking.id}</div>
                <div>Avatar: {booking.avatar}</div>
                <div>Service: {booking.service}</div>
                <div>Status: {booking.status}</div>
                <div>Date: {booking.date}</div>
                <button onClick={() => requestCancellation(booking.id)}>Request Cancellation</button>
            </div>
        ));
    };

    return (
        <div className="view-booking">
            <h1>Danh sách phiếu đặt lịch khám</h1>
            <h2>Hiện tại</h2>
            <div className="booking-list">{renderBookings(currentBookings)}</div>
            <div className="schedule">
                <h2>Tương lai</h2>
                <div className="booking-list">{renderBookings(bookingList)}</div>
            </div>
        </div>
    );
};

export default ViewBooking;
